<?php// Add and remove scripts and styles// Post Typesinclude 'inc/post-types/announcements.php';function remove_unwanted_scripts_and_styles_at_wp_enqueue_scripts() {	// Remove scripts	// Replace the “portion” part of any handle with the name of your Underscores parent theme	wp_dequeue_script( 'portion-navigation' );	wp_deregister_script( 'portion-navigation' );	// Remove styles	wp_dequeue_style( 'portion-style' );	wp_deregister_style( 'portion-style' );	wp_dequeue_style( 'wp-block-library' );	wp_deregister_style( 'wp-block-library' );	wp_dequeue_style( 'wp-block-library-theme' );	wp_deregister_style( 'wp-block-library-theme' );	wp_dequeue_style( 'wc-block-style' );	wp_deregister_style( 'wc-block-style' );	wp_dequeue_style( 'global-styles' );	wp_deregister_style( 'global-styles' );	$parent_style = 'parent-main-style';	// Add parent theme stylesheet	wp_enqueue_style( 'parent-main-style', get_template_directory_uri() . '/style.css' );	// Add Custom CSS	//wp_enqueue_style( 'c5-main-custom-style', get_stylesheet_directory_uri() . '/custom.css',		//array($parent_style), wp_get_theme()->get('Version') );	// Add child theme stylesheet	wp_enqueue_style( 'c5-main-child-style', get_stylesheet_uri() );}add_action( 'wp_enqueue_scripts',	'remove_unwanted_scripts_and_styles_at_wp_enqueue_scripts',	100 );// Remove wpemoji script and stylesremove_action( 'wp_head', 'print_emoji_detection_script', 7 );remove_action( 'wp_print_styles', 'print_emoji_styles' );// Remove WP-inserted svgs at start of bodyremove_action( 'wp_body_open', 'wp_global_styles_render_svg_filters' );register_nav_menus(	array(		'menu-2' => esc_html__('Classes', 'c5connections-theme' ),	));// User Sites Menufunction get_user_sites_menu() {	$current_user = wp_get_current_user();	if ($current_user->ID == 0) return; // Not logged in.		// Get blogs of the current user.	$user_blogs = get_blogs_of_user($current_user->ID);		// Loop through the blogs and create links.	foreach ($user_blogs as $user_blog) {		echo '<li><a href="' . $user_blog->siteurl . '">' . $user_blog->blogname . '</a></li>';	}}